<template>
  <div class="login-wrapper">
    <div class="login-form">
      <h3 class="title">iForms</h3>
      <el-form
        ref="form"
        :model="form"
        :rules="rules"
        label-position="top"
        class="form-area"
        @submit.native.prevent
        @keyup.enter="handleSubmit('form')"
      >
        <el-form-item label="用户名" prop="username">
          <el-input v-model="form.username" autocomplete="on" />
        </el-form-item>
        <el-form-item label="密码" prop="password">
          <el-input v-model="form.password" type="password" />
        </el-form-item>
        <el-form-item label="验证码" prop="code">
          <el-input v-model="form.code" auto-complete="off" style="width: 60%" />
          <div class="login-code">
            <img :src="data.codeUrl" alt="" @click="getCode">
          </div>
        </el-form-item>
        <el-form-item>
          <el-button
            class="login-btn"
            type="primary"
            native-type="submit"
            @click="handleSubmit('form')"
          >立即登录</el-button>
        </el-form-item>
      </el-form>
    </div>
    <div class="login-logo" />
    <!-- <div class="login-github">
      <a class="icon-github" href="https://github.com/52admln/vue-questionnaire" target="_blank">
        <img
          src="data:image/svg+xml;base64,"
          alt="GitHub"
        ></a>
    </div> -->
  </div>
</template>

<script lang="ts">
import { Component, Vue } from 'vue-property-decorator'
import { Form as ElForm } from 'element-ui'

import { UserModule } from '@/store/modules/user'
import * as AdminAction from '@/api/auth'

@Component
export default class LoginComponent extends Vue {
  data = {
    codeUrl: '',
    cookiePass: ''
  }
  private form = {
    username: '',
    password: '',
    code: '',
    uuid: ''
  }
  private rules = {
    username: [
      {
        required: true,
        message: '请输入用户名',
        trigger: 'blur'
      }
    ],
    password: [
      {
        required: true,
        message: '请输入密码',
        trigger: 'blur'
      }
    ],
    code: [
      {
        required: true,
        message: '请输入验证码',
        trigger: 'blur'
      }
    ]
  }
  async getCode () {
    const res = await AdminAction.getCodeImg()
    if (res.success) {
      this.data.codeUrl = res.data.img
      this.form.uuid = res.data.uuid
    }
  }

  created () {
    this.getCode()
  }

  handleSubmit (name: string) {
    (this.$refs[name] as ElForm).validate(async (valid) => {
      if (valid) {
        const res = await AdminAction.login({
          ...this.form
        })
        if (res.success) {
          this.$message.success('登录成功!')
          const data: any = {
            username: this.form.username,
            token: res.data
          }
          UserModule.login(data)
          await this.$router.push('/list')
        } else {
          this.$message.error('登录失败，请检查账号密码是否正确。')
        }
      } else {
        this.$message.error('表单填写有误!')
      }
    })
  }
}
</script>

